mapper.select:
SELECT
	COUNT(*) cnt
FROM
	REG_USER
WHERE
	USER_STATUS = #{userStatus, jdbcType=VARCHAR}

		AND UNIQ_ID = #{uniqId, jdbcType=VARCHAR}


		AND USER_ID = #{userId, jdbcType=VARCHAR}

SELECT
	UNIQ_ID
	, USER_ID
	, PASSWORD
FROM
	REG_USER
WHERE
	USER_STATUS = #{userStatus, jdbcType=VARCHAR}

		AND UNIQ_ID = #{uniqId, jdbcType=VARCHAR}


		AND USER_ID = #{userId, jdbcType=VARCHAR}
